import "./Form.css";
const Form = () => {

  
  const currentSavingHandler = (event) => {
    console.log(event.target.value);
  };

  const yearlySavingHandler = (event) => {
    console.log(event.target.value);
  };

  const interestHandler = (event) => {
    console.log(event.target.value);
  };

  const yearDurationHandler = (event) => {
    console.log(event.target.value);
  };

  return (
    <form className="form">
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            type="number"
            onChange={currentSavingHandler}
            id="current-savings"
          />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            type="number"
            onChange={yearlySavingHandler}
            id="yearly-contribution"
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input
            type="number"
            onChange={interestHandler}
            id="expected-return"
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input type="number" onChange={yearDurationHandler} id="duration" />
        </p>
      </div>
      <p className="actions">
        <button type="reset" className="buttonAlt">
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
};
export default Form;
