import "./Form.css";
import React, { useState } from "react";

const Form = (props) => {


  const [userInput, setUserInput] = useState({
    "current-savings": 1000,
    "yearly-contribution": 1000,
    "expected-return": 5,
    duration: 24,
  });

  const submitHandler = (event) => {

    event.preventDefault();    
    props.calculate(userInput);

  };


  

  const resetHandler = (event) => {
    setUserInput({
      "current-savings": 1000,
      "yearly-contribution": 1000,
      "expected-return": 5,
      duration: 24,
    });
  };

  const inputHandler = (input, value) => {
    setUserInput((prevState) => {
      return {
        ...prevState,
        [input]: value,
      };
    });
  };

  

  return (
    <form className="form">
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            type="number"
            onChange={(event) => {
              inputHandler("current-savings", event.target.value);
            }}
            value={userInput["current-savings"]}
            id="current-savings"
          />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            type="number"
            onChange={(event) => {
              inputHandler("yearly-contribution", event.target.value);
            }}
            value={userInput["yearly-contribution"]}
            id="yearly-contribution"
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input
            type="number"
            value={userInput["expected-return"]}
            onChange={(event) => {
              inputHandler("expected-return", event.target.value);
            }}
            id="expected-return"
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input
            type="number"
            onChange={(event) => {
              inputHandler("duration", event.target.value);
            }}
            value={userInput["duration"]}
            id="duration"
          />
        </p>
      </div>
      <p className="actions">
        <button type="reset" onClick={resetHandler} className="buttonAlt">
          Reset
        </button>
        <button type="submit" onSubmit={submitHandler} className="button">
          Calculate
        </button>
      </p>
    </form>
  );
};
export default Form;
